    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        // create variable for first input 
        String firstNumber = input.getText();
        //create variable for second input
        String secondNumber = input1.getText();
        //create int variable for second number
        int secondNumberr = Integer.parseInt(secondNumber);
        //create int variable for first number
        int firstNumberr = Integer.parseInt(firstNumber);
        // make first number subtract from second number
        int firstNumb = firstNumberr - secondNumberr;
        // output the result
        output.setText ("" + firstNumb);
        
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        
        String firstNumber = input.getText();
        
        String secondNumber = input1.getText();
        
        int secondNumberr = Integer.parseInt(secondNumber);
        
        int firstNumberr = Integer.parseInt(firstNumber);
        // multiply first number to second number
        int firstNumb = firstNumberr * secondNumberr;
        // output the result
        output.setText ("" + firstNumb);
        
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        String firstNumber = input.getText();
        
        String secondNumber = input1.getText();
        
        int secondNumberr = Integer.parseInt(secondNumber);
        
        int firstNumberr = Integer.parseInt(firstNumber);
        // divide the first number to the second
        int firstNumb = firstNumberr / secondNumberr;
        // output the result
        output.setText ("" + firstNumb);
        
        
    }//GEN-LAST:event_jButton4ActionPerformed

