    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        String firstNumber = input.getText();
        
        String secondNumber = input1.getText();
        
        int secondNumberr = Integer.parseInt(secondNumber);
        
        int firstNumberr = Integer.parseInt(firstNumber);
        // add the first and second number with each other
        int firstNumb = firstNumberr + secondNumberr;
        // output the sum
        output.setText ("" + firstNumb);
    }//GEN-LAST:event_jButton1ActionPerformed

